class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> dum(k+2,0);
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            int num1=nums[i];
            int num2=nums[n-i-1];
            int diff=abs(num1-num2);
            if(diff!=0){
                dum[0]++;
                dum[diff]--;
                // cout<<0<<" "<<diff-1<<endl;
            }
            int mini=diff+1;
            int maxa=max(k-min(num1,num2),max(num1,num2));
            if(maxa>=mini){
                dum[mini]++;
                dum[maxa+1]--;
                // cout<<mini<<" "<<maxa<<endl;
            }
            if(maxa!=k){
                dum[maxa+1]+=2;
                dum[k+1]-=2;
                // cout<<maxa+1<<" "<<k<<endl;
            }
            // cout<<endl;
        }
        int ans=dum[0];
        for(int i=1;i<k+1;i++){
            dum[i]+=dum[i-1];
            ans=min(ans,dum[i]);
        }
        return ans;
    }
};
