class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();

        int ans = n;

        vector <int> dp(k + 1, 0);
        unordered_map <int, int> mp;

        for(int i = 0; i < (n/2); i++){

            int x = nums[i];
            int y = nums[n - 1 - i];

            if(x > y)swap(x, y);

            int cur = max(y, k - x);
            dp[cur]++;

            mp[ abs(y - x) ]++;
        }

        for(int i = 1; i <= k; i++){
            dp[i] += dp[i - 1];
        }

        // for(auto x: dp)cout<<x<<" ";
        // cout<<endl;

        // for(int i = 0; i <= k; i++)cout<<mp[i]<<" ";
        // cout<<endl;

        for(int i = 0; i <= k; i++){

            int l = 0, r = 0, mid = 0;

            if(i > 0)l = dp[i - 1];

            r = dp[k] - l;

            if(mp.count(i)){
                r -= mp[i];
            }

            int cur = 2*l + r;
            ans = min(ans, cur);

            // cout<<i<<" "<<l<<" "<<r<<" "<<cur<<" || ";
        }

        return ans;
    }
};
