class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> mp;
        //对于x<k/2.就考虑频率最大的.
        //对于k/2<=x<=k.记录每个x的频率,以及dif不等于x的对中,有多少个需要同时动两个数字.
        vector<int> v(k+1,0); //记录一个差分
        vector<int> f(k+1,0);
        for(int i = 0;i<n/2;i++)
        {
            int a = nums[i],b = nums[n-i-1];
            mp[abs(a-b)]++;
            //只改一个数,可达的最大dif是
            int maxdif = max({a,b,k-a,k-b});
            if(maxdif<k) v[maxdif+1]++;
        }
        //差分数组
        for(int i = 1;i<=k;i++) v[i]+=v[i-1];
        for(auto & [kk,vv]:mp) f[kk] = vv;
        int ans = n;
        for(int i = 0;i<=k;i++)
        {
            //枚举x = i.
            int cg = n/2-f[i];//需要改变的对
            int db = v[i];//需要双倍的对数
            ans = min(ans,cg+db);
        }
        return ans;
    }
};
