class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> counts;
        vector<int> maxes;
        for (int i = 0; i < n/2; i++) {
            counts[abs(nums[i]-nums[n-i-1])]++;
            maxes.push_back(max(max(nums[i], nums[n-i-1]), k-min(nums[i], nums[n-i-1])));
        }
        sort(maxes.begin(), maxes.end());
        int res = 1e9;
        for (auto [key, val] : counts) {
            int total = n/2-val;
            auto it = lower_bound(maxes.begin(), maxes.end(), key);
            int doubles = it-maxes.begin();
            res = min(res, doubles*2+(total-doubles));
        }
        return res;
    }
};
