class Solution {
public:
    int minChanges(vector<int>& A, int k) {
        map<int, int> diff;
        vec<int> cc(1e5+2);
        rep(i, A.size() / 2) {
            int a = min(A[A.size() - i - 1], A[i]);
            int b = max(A[A.size() - i - 1], A[i]);
            diff[b - a]++;
            int mx = (b - a) + max(a, k - b);
            cc[mx]++;
        }

        for (int i = cc.size() - 2; i >= 0; --i) cc[i] += cc[i + 1];
        int res = numeric_limits<int>::max();
        int half = A.size() / 2;
        for (const auto& [key, val]: diff) {
            int t = cc[key];
            chmin(res, (half - t) * 2 + t - val);
        }

        return res;
    }
};
