class Solution:
    def minChanges(self, a: List[int], k: int) -> int:
        n = len(a)
        d = defaultdict(list)
        for i in range(n // 2):
            val = abs(a[i] - a[n - i - 1])
            d[val].append((max(a[i], a[n - i - 1]), min(a[i], a[n - i - 1])))
        arr = []
        for v in d:
            arr.append((-len(d[v]), v))
        arr.sort()
        # print(d)
        # print(arr)
        last = 1
        ans = inf
        for x, y in arr:
            if x !=  last:
                tot = 0
                for v in d:
                    if v != y:
                        for big, small in d[v]:
                            if k - small < y and big < y:
                                tot += 2
                            else:
                                tot += 1
                ans = min(ans, tot)
                last = x
        return ans
