class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] diff = new int[100001];
        for(int i=0;i<n/2;i++){
            diff[(int)Math.abs(nums[i]-nums[n-i-1])]++;
        }
        int diffreq = -1;
        int count = 0;
        int pre = -1;
        for(int i=0;i<100001;i++){
            if(diff[i]>count){
                count = diff[i];
                pre= diffreq;
                diffreq = i;
            }
        }
        // System.out.println("req diff = "+diffreq);
        int ans= 0;
        for(int i=0;i<n/2;i++){
            // System.out.println("comparing "+nums[i]+" and "+nums[n-i-1]);
            if((int)Math.abs(nums[i]-nums[n-i-1])!=diffreq){
                if((nums[i]-diffreq)<0 && (nums[i]+diffreq)>k && (nums[n-i-1]-diffreq)<0 && (nums[n-i-1]+diffreq)>k)ans+=2;
                else ans += 1;
            }
            // System.out.println(ans);
        }
        diffreq = pre;
        // System.out.println("req diff = "+diffreq);
        int ans2= 0;
        for(int i=0;i<n/2;i++){
            // System.out.println("comparing "+nums[i]+" and "+nums[n-i-1]);
            if((int)Math.abs(nums[i]-nums[n-i-1])!=diffreq){
                if((nums[i]-diffreq)<0 && (nums[i]+diffreq)>k && (nums[n-i-1]-diffreq)<0 && (nums[n-i-1]+diffreq)>k)ans2+=2;
                else ans2 += 1;
            }
            // System.out.println(ans);
        }
        return (int)Math.min(ans,ans2);
    }
}
