int ss[100005];
int sx[100005];
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int i;
        memset(ss,0,sizeof(ss));
        memset(sx,0,sizeof(sx));
        for (i = 0; i < n / 2; i++) {
            int d = abs(nums[i]-nums[n-i-1]);
            ss[abs(nums[i]-nums[n-i-1])]++;
            int x = max(nums[i],nums[n-i-1]);
            int y = min(nums[i],nums[n-i-1]);
           // cout<<x<<" "<<y<<" "<<d<<endl;
            if (k - x > y) {
                sx[d + (k - x) + 1] --;
            } else {
                sx[d + y + 1] --;
            }
        }
        sx[0] = n / 2;
        for (i = 1; i <= 100000; i++) {
            sx[i] += sx[i - 1];
        }
        int ans = 2000001;
        for (i = 0; i <= 100000; i++) {
            int s1 = sx[i];
            int s2 = n/2 - sx[i];
            //cout<<s1<<" "<<s2<<" "<<ss[i]<<endl;
           // if (i > k) break;
            ans = min(ans, s1 - ss[i] + s2 * 2);
          //  cout<<i<<" "<<ans<<endl;
        }
        return ans;
    }
};
