class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> arr(k+5,0);
        int n=nums.size();
        for(int i=0;i<(n/2);i++)
        {
            int num=max(max(nums[i],nums[n-1-i]),k-min(nums[i],nums[n-1-i]));
            arr[num+1]++;
        }  
        for(int i=1;i<arr.size();i++) arr[i]+=arr[i-1];
        int ans=INT_MAX;
        map<int,int> mp;
        for(int i=0;i<(n/2);i++) mp[abs(nums[i]-nums[n-1-i])]++;
        for(auto itr:mp)
        {
            ans=min(ans,(n/2)-itr.second+arr[itr.first]);
        }   
        return ans;
    }
};
