class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> diffs;
        vector<int> rfls;
        for (int i = 0; i <= n - i - 1; ++i) {
            ++diffs[abs(nums[i] - nums[n - i - 1])];
            int d1 = max(abs(nums[i]), abs(k - nums[i]));
            int d2 = max(abs(nums[n - i - 1]), abs(k - nums[n - i - 1]));
            rfls.push_back(max(d1, d2));
        }
        sort(rfls.begin(), rfls.end());
        int ptr = 0;
        int ans = 1e9;
        for (auto [diff, freq] : diffs) {
            while (ptr < rfls.size() && rfls[ptr] < diff) ++ptr;
            ans = min(ans, n / 2 - freq + ptr);
        }
        return ans;
    }
};
