class Solution {
 public:
  int minChanges(vector<int>& nums, int k) {
    int n = nums.size(), diff[k + 2], ans = n;
    memset(diff, 0, sizeof(diff));
    for (int i = 0, j = n - 1; i < j; i++, j--) {
      diff[0] += 2, diff[k + 1] -= 2;
      int l = abs(nums[i] - nums[j]),
          r = max({nums[i], nums[j], k - nums[i], k - nums[j]});
      diff[0]--, diff[r + 1]++;
      diff[l]--, diff[l + 1]++;
    }
    for (int i = 0, sum = 0; i <= k; i++) {
      sum += diff[i];
      ans = min(ans, sum);
    }
    return ans;
  }
};
