class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int>m;
        for(int i=0;i<n/2;i++) {
            if(nums[i]>nums[n-i-1])swap(nums[i],nums[n-i-1]);
            m[-nums[i]+nums[n-i-1]]++;
        }
        int ans=n;
        vector<pair<int,int>>v;
        for(auto it : m) {
            v.push_back({it.second,it.first});
        }
        sort(v.rbegin(),v.rend());
        for(auto it : v) {
            int cnt=it.first;
            int d = it.second;
            int minHere = n/2-cnt;
            if(ans<=minHere)break;
            int here=n;
            for(int i=0;i<n/2;i++) {
                int a=nums[i];
                int b=nums[n-i-1];
                if(b-a==d){
                    here-=2;
                    continue;}
                if((b-0>=d and b-k<=d)||(0-a<=d and k-a>=d))here--;

            }
            ans=min(ans,here);
        }
        return ans;
    }
};
