class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        int mx[n], mi[n];
        map<int,int> mp;
        for(int i=0;i<n/2;i++){
            mp[abs(a[i]-a[n-i-1])]++;
            mx[i] = max(a[i],k-a[i]);
            mx[i] = max(a[n-i-1], mx[i]);
            mx[i] = max(k-a[n-i-1], mx[i]);
            // mi[i] = min(a[i],k-a[i]);
            // mi[i] = min(a[n-i-1], mi[i]);
            // mi[i] = min(k-a[n-i-1], mi[i]);
            mi[i] =0;

        }

        // for(int i=0;i<n/2;i++)
        // cout<<mi[i]<<" "<<mx[i]<<endl;

        int p[k+1];
        for(int i=0;i<=k;i++){
            p[i]=0;
        }      
        for(int i=0; i<n/2;i++){
            p[mi[i]]++;
            if(mx[i]!=k)
            p[mx[i]+1]--;
        }
        for(int i=0;i<=k;i++){
            p[i] += (i==0?0:p[i-1]);
        }        
        int ans = n;
        for(int i=0;i<=k;i++){
            int cnt = 0;
            cnt += (p[i] + (n/2 - p[i])*2);
            cnt -= mp[i];
            ans= min(ans,cnt);
        }
        return ans;
    }
};
