class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<ll> c(k + 10);
        int n = nums.size();
        for(int i = 0; n - i - 1 > i; i++) {
            int l = nums[i] - 0;
            l = max(l, k - nums[i]);
            l = max(l, nums[n - i - 1]);
            l = max(l, k - nums[n - i - 1]);
            int r = abs(nums[i] - nums[n - i - 1]);
            c[0]++;
            c[l + 1]++;
            c[r]--;
            c[r + 1]++;
            // for(int i = 0; i <= k; i++) {
            //     cout  << c[i] << " ";
            // }
            // cout << "\n";
        }

        for(int i = 1; i <= k; i++) {
            c[i] += c[i - 1];
            //cout << c[i] << "\n";
        }
        ll ans = 1e9;
        for(int i = 0; i <= k; i++) {
            ans = min(ans, c[i]);
        }
        return ans;

    }
};
