class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

            map<int,int> mp,mp2;
            int n  = nums.size();
        int mi   =  1e9;

        for(int x=0;x<nums.size()/2;x++)
        {
            mp[max({k-nums[x],k-nums[n-x-1],nums[x],nums[n-x-1]})]++;
            mp2[abs(nums[x]-nums[n-x-1])]++;

        }

        vector<int> pref(k+2,0);

        for(auto it:mp)
        {
            pref[it.first] = it.second;
        }

        vector<int> suf(k+2,0);

        for(int x=0;x<pref.size();x++)
        {
            //cout << pref[x] << " ";
            suf[x] = pref[x];
        }
        //cout << endl;

        for(int x=suf.size()-2;x>=0;x--)
        {
            suf[x] += suf[x+1];
        } 



        for(int x=1;x<pref.size();x++)
            pref[x] += pref[x-1];



        int ans = 1e9;

        for(int x=k;x>=0;x--)
        {

            int lef = 0;
            if(x-1>=0)
                lef = pref[x-1];
            int rig = 0;
            rig = suf[x] - mp2[x];

            ans = min(ans,2*lef+rig);

        }

        return ans;




    }
};
