constexpr int maxK = 1e5+7;
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> kline(maxK, 0);
        int n = nums.size();
        for(int i = 0; i < n / 2; i++){
            auto gt = max(nums[i], nums[n - i - 1]), ls = min(nums[i], nums[n - i - 1]);
            auto thres1 = gt - ls, thres2 = max(k - ls, gt);
            // printf("%d %d %d %d\n", gt, ls, thres1, thres2);
            kline[thres1] -= 1;
            kline[thres1 + 1] += 1;
            kline[thres2 + 1] += 1;
        }

        auto ans = n, init = n / 2;
        for(int i = 0; i < maxK; i++){
            // printf("%d %d\n", i, kline[i]);
            init += kline[i];
            ans = min(ans, init);
        }

        return ans;
    }
};
