class Solution {
    public int minChanges(int[] nums, int k) {
            int ans = Integer.MAX_VALUE ;
            int n = nums.length;
            TreeMap<Integer,Integer> tm = new TreeMap<>();
            for (int i=0;i<n/2;i++){
                int diff = Math.abs(nums[i]-nums[n-i-1]);
                tm.put(diff,tm.getOrDefault(diff,0)+1);
            }
            PriorityQueue<Integer> pq = new PriorityQueue<>((a,b)->tm.get(b)-tm.get(a));
            for (Integer num : tm.keySet()) pq.add(num);
            int max1 = pq.poll();
            int max2 = 0;
            if (!pq.isEmpty()) max2 = pq.poll();


            int c1 = 0;

            for (int i=0;i<n/2;i++){
                int diff = Math.abs(nums[i]-nums[n-i-1]);
                if (diff==max1) continue;
                int[] arr = new int[4];
                arr[0] = nums[i] + max1;
                arr[1] = nums[i] - max1;
                arr[2] = nums[n-i-1] + max1;
                arr[3] = nums[n-i-1] - max1;
                boolean chck = false;
                for (Integer num : arr){
                    if (num>=0 && num<=k) chck = true;
                }
                if (chck) c1++;
                else c1+=2;
            }

        int c2 = 0;

        for (int i=0;i<n/2;i++){
            int diff = Math.abs(nums[i]-nums[n-i-1]);
            if (diff==max2) continue;
            int[] arr = new int[4];
            arr[0] = nums[i] + max2;
            arr[1] = nums[i] - max2;
            arr[2] = nums[n-i-1] + max2;
            arr[3] = nums[n-i-1] - max2;
            boolean chck = false;
            for (Integer num : arr){
                if (num>=0 && num<=k) chck = true;
            }
            if (chck) c2++;
            else c2+=2;
        }

        // System.out.println(c1+" " + c2 + " " + max1 + " " + max2);



            return Math.min(c1,c2);
    }
}
