class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        map<int,int> mp;
        map<int,int> mp2;
        for(int i = 0; i < n/2; i ++){
            mp[abs(a[i] - a[n-1-i])]++;
            int t = max(max(abs(k-a[n-1-i]), abs(0-a[n-1-i])), max(abs(a[i] - 0), abs(a[i]-k)));
            t = max(t, abs(a[i] - a[n-1-i]));
            mp2[t]++;
        }
        int nn = n/2;

        int last = 0;
        for(auto &iter : mp2){
            iter.second += last;
            last = iter.second;
        }

        int ans = n;
        for(auto &[x, y] : mp){
            auto iter = mp2.lower_bound(x);
            if(iter == mp2.begin()){
                ans = min(ans, nn-y);
                continue;
            }
            iter--;
            ans = min(ans, 2*(iter->second) + nn-iter->second-y);
        }
        return ans;


    }
};
