class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> map;
        vector<int> count_x(k+1,0);
        for (int i=0;i<n/2;i++){
            map[abs(nums[i]-nums[n-i-1])]++;
            int l=k-min(nums[i],nums[n-i-1]);
            int r=max(nums[i],nums[n-i-1]);
            count_x[max(l,r)]++;
        }
        vector<int> sum(k+1,0);
        sum[k]=count_x[k];
        for (int i=k-1;i>=0;i--)
            sum[i]=sum[i+1]+count_x[i];
        int ans=n;
        for (const auto &pair:map){
            int value=pair.first;
            int time=pair.second;
            ans=min(ans,sum[value]+2*(n/2-sum[value])-time);
        }
        return ans;
    }
};
