class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>v(n/2);
        for(int i=0;i<n/2;i++) {
            v[i]=max(max(nums[i],k-nums[i]),max(nums[n-i-1],k-nums[n-i-1]));
        }
        sort(v.begin(),v.end());
        map<int,int>p;
        for(int i=0;i<n/2;i++) {
            int d=(abs(nums[i]-nums[n-i-1]));
            p[d]++;
        }
        int res=INT32_MAX;
        for(int i=0;i<n/2;i++) {
            int d=(abs(nums[i]-nums[n-i-1]));
            int c=lower_bound(v.begin(),v.end(),d)-v.begin();
            int r=n/2+c-p[d];
            res=min(res,r);
        }
        return res;
    }
};
