class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> m;
        vector<int> v;
        for(int i=0;i<nums.size()/2;i++)
        {
            m[abs(nums[i]-nums[nums.size()-i-1])]++;
            int x=nums[i];
            int y=nums[nums.size()-i-1];
            int z=max(max(x,y),k-min(x,y));
            v.push_back(z);
        }
        sort(v.begin(),v.end());
        int mx=0,a=INT_MAX;
        for(auto it:m)
        {
            //mx=max(mx,it.second);
            int in=lower_bound(v.begin(),v.end(),it.first)-v.begin();
            a=min(a,(int) (nums.size()/2-it.second+in));
        }
        return a;
    }
};
