class Solution {
private:
    #define INF (int)pow(10, 9)
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> d(n / 2, 0);
        for(int i = 0; i < n / 2; i++)
        {
            int mini = min(nums[i], nums[n - i - 1]);
            int maxi = max(nums[i], nums[n - i - 1]);
            d[i] = max({mini, maxi, k - maxi, k - mini});
        }
        sort(d.begin(), d.end());
        unordered_map<int, int> mp;
        for(int i = 0; i < n / 2; i++)
            mp[abs(nums[i] - nums[n - i - 1])]++;
        int ans = INF;

        for(int x = 0; x <= k; x++)
        {
            int lb = lower_bound(d.begin(), d.end(), x) - d.begin();
            int curr = 2 * lb;
            curr += (n / 2 - lb);
            curr -= mp[x];
            ans = min(ans, curr);
        }
        return ans;
    }
};
