class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> hash(k+1,0);
        vector<int> oneChange(k+1,0);
        vector<int> Dup(k+1,0);
        int size=nums.size(),ma;
        for(int i=0;i<size/2;i++)
        {
           hash[abs(nums[i]-nums[size-1-i])]++;
           ma=k-nums[i];
           ma=max(ma,nums[i]);
           ma=max(ma,k-nums[size-1-i]);
           ma=max(ma,nums[size-1-i]);
           oneChange[ma]++;
           if(ma>=abs(nums[i]-nums[size-1-i]))Dup[abs(nums[i]-nums[size-1-i])]++;
        }
        int min_change=INT_MAX;
        for(int i=k;i>=0;i--)
        {
            if(i!=k)oneChange[i]+=oneChange[i+1];
            int dup=oneChange[i]-Dup[i];
            min_change=min(min_change,dup+2*((size/2)-dup-hash[i]));
        }
        return min_change;
    }
};
