class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> dif,cnt;
        for(int i = 0; i < n/2; i++){
            int x = nums[i], y = nums[n-i-1];
            if(x > y) swap(x,y);
            int mxo = max(y,k-x);
            dif[mxo+1]--;
            //cout << abs(x-y) << " to " << mxo << "\n";
            dif[0]++;
            dif[abs(x-y)] += 0;
            cnt[abs(x-y)]++;
        }
        int cum = 0,ans = 1e9;
        for(auto[x,d]:dif){
            cum += d;
            int twos = n/2 - cum;
            int ones = cum-cnt[x];
            //cout << x << " " << twos << " " << ones << " " << cum << "\n";
            ans = min(ans,twos*2+ones);
        }
        return ans;
    }
};
