class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        const int  n = nums.size();
        vector<int> v(k+2,0), u(k+1,0);
        for(int i=0;2*i<n;++i){
            int a = nums[i], b = nums[n-1-i], d  = abs(a-b);
            int mx = max({ k-a, k-b, a, b});
            u[d]++;
            v[0]++;
            v[mx+1]++;
        }
        int ans = n ;
        for(int i=1;i<=k;++i) v[i]+=v[i-1];
        for(int i=0;i<=k;++i) ans = min(ans, v[i]-u[i]);
        return ans;
    }
};
