class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        ls = []
        cnt = Counter()
        for i in range(n // 2):
            v = abs(nums[i] - nums[n - i - 1])
            cnt[v] += 1
            ls.append(v)
        ans = None
        for num, v in sorted(cnt.items(), key=lambda x: x[1], reverse=True):
            if ans is not None and (n // 2 - v) >= ans:
                break
            tmp = 0
            for i in range(n // 2):
                if ls[i] == num:
                    continue
                elif nums[i] <= nums[n - i - 1] and (0 <= (nums[n - i - 1] - num) <= k or 0 <= (nums[i] + num) <= k):
                    tmp += 1
                elif nums[i] > nums[n - i - 1] and (0 <= (nums[i] - num) <= k or 0 <= (nums[n - i - 1] + num) <= k):
                    tmp += 1
                else:
                    tmp += 2
            if ans is None or ans > tmp:
                ans = tmp
        return ans
