class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> hash;
        int x=n/2,maxval=0;
        vector<int> ret(x);
        for(int i=0;i<x;i++)
        {
            int y=abs(nums[i]-nums[n-i-1]);
            if(nums[i]>nums[n-i-1])
                swap(nums[i],nums[n-i-1]);
            ret[i]=max(nums[i],k-nums[n-i-1])+y;
            maxval=max(maxval,y);
            hash[y]++;
        }
        sort(ret.begin(),ret.end());
        int count=1e7,left=0;
        for(int i=0;i<=maxval;i++)
        {
            while(left<x && ret[left]<i)
                left++;
            count=min(count,x+left-hash[i]);
        }
        return count;
    }
};
