class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> b(k + 2,0);
        map<int,int> cnt;
        for(int i = 0; i < nums.size() / 2;i++) {
            int maxn = max(nums[i], nums[n - i - 1]);
            int minn = min(nums[i], nums[n - i - 1]);
            cnt[maxn - minn]++;
            int fan = max(maxn, k - minn);
            b[0] += 1;
            b[fan + 1] -= 1;
        }
        int res = 0x3f3f3f3f;
        int sum = 0;
        for(int x = 0; x <= k; x++) {
            sum += b[x];
            res = min(res, sum + 2 * (n / 2 - sum) - cnt[x]);
        }
        return res;
    }
};
