class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> pre(k+1,0)      ;

        int n=nums.size();
        int res = n-1;
        map<int,int> mp;
        for(int i=0;i<n/2;i++)
        {
             int diff = max({nums[i],nums[n-i-1], k-nums[i],k-nums[n-i-1]});
            // cout<<diff<<endl;
             pre[diff]++;
            mp[abs(nums[i]-nums[n-i-1])]++;
        }


        for(int i=1;i<=k;i++){
            pre[i]+=pre[i-1];
        }

        for(int i=0;i<n/2;i++){
             int val = abs(nums[i]- nums[n-i-1]);
             int cost = 0;
             if(val>0){
                 cost += pre[val-1]*2;
             }

                 cost += pre[k]- ((val>0) ? pre[val-1]: 0);
                 cost -= mp[val];

            res = min(res,cost);

        }


        return res;
    }
};


