class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        int n=v.size();
        vector<int>a(k+5);
         vector<ll>b(k+5);
         vector<ll>c(k+5);

        ll i=0,j=n-1;
        while(i<j){
            ll x=v[i],y=v[j];

            ll l=x;
            l=max(l,k-x);
            l=max(l,y);
            l=max(l,k-y);


            a[0]++;
            a[l+1]--;

            b[l+1]++;
            c[abs(x-y)]++;
            a[abs(x-y)]--;
            a[abs(x-y)+1]++;

            i++;
            j--;
        }

        for(int i=1;i<=k+1;i++){
            a[i]+=a[i-1];
        }

        for(int i=1;i<=k+1;i++){
            b[i]+=b[i-1];
        }

        // printv(a);
        // printv(b);
        ll mn=1e18;
        for(int i=0;i<=k;i++){
            mn=min(mn,a[i]+b[i]*2);
        }

        return mn;
    }
};
