class Solution {
public:
    int func(int var, vector<int>& nums, int k){
        int l=0,r=nums.size()-1;
        int ans=0;
        while(l<r){
            if(abs(nums[r]-nums[l])!=var){
                int a=0, b=k;
                if(max(nums[r],nums[l])-a>=var){
                    ans++;
                }
                else if(b-min(nums[r],nums[l])>=var){
                    ans++;
                }
                else{
                    ans+=2;
                }
            }
            l++;
            r--;
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp;
        int l=0,r=nums.size()-1;
        while(l<r){
            mp[abs(nums[r]-nums[l])]++;
            l++;
            r--;
        }
        priority_queue<pair<int,int>>pq;
        for(auto x:mp){
            pq.push({x.second,x.first});
        }
        int ans=INT_MAX;
        int i=0;
        while(!pq.empty()&&i<20){
            int a=pq.top().second;
            pq.pop();
            ans=min(ans,func(a,nums,k));
            i++;
        }
        return ans;

    }
};
