class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        counter = Counter()
        most = 0
        for i in range(len(nums)//2):
            diff = abs(nums[i] - nums[-1 -i])
            if diff in counter:
                counter[diff] += 1
            else:
                counter[diff] = 1


        most = counter.most_common(3)

        ans = inf
        for m in most:
            best = m[0]
            current = 0
            for i in range(len(nums)//2):
                left = nums[i]
                right = nums[-1 -i]
                maximum = max(max(left, right), k - min(left, right))
                diff = abs(left - right)

                if diff != best:
                    if maximum < best:
                        current += 2
                    else:
                        current += 1
            ans = min(ans, current)
        return ans
