class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>v(n/2,0);
        map<long long ,long long>mpp;
        for(int i=0;i<n/2;i++){
            mpp[abs(nums[i]-nums[n-i-1])]++;
            v[i]=max(max(nums[i],nums[n-i-1]),k-min(nums[i],nums[n-i-1]));
        }
        sort(v.begin(),v.end());
        // for(int i=0;i<v.size();i++){
        //     cout<<v[i]<<" ";
        // }
        int ans=n;
        for(int i=0;i<=k;i++){
            int lb=lower_bound(v.begin(),v.end(),i)-v.begin();
            int x=mpp[i];
            ans=min(ans,n/2-lb+2*lb-x);
        }
        return ans;
    }
};
