class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> pref(k + 1);

        for(int i = 0; i < nums.size() / 2; i++){
            int x = min(nums[i], nums[nums.size() - i - 1]);
            int y = max(nums[i], nums[nums.size() - i - 1]);

            int dif = y - x;
            if(dif == 0){
                int gap = max(k - x, y);

                if(k > 0)
                    pref[1]++;
                if(gap != k){
                    pref[gap + 1]++;    
                }


            }
            else if(dif == k){
                pref[dif]--;
                pref[0]++;
            }
            else {
                pref[0]++;

                pref[dif]--;
                pref[dif + 1]++;


                int gap = max(k - x, y);

                if(gap != k){
                    pref[gap + 1]++;    
                }

            }
        }
        int mini = 1e9, curr = 0;
        for(int i = 0; i <= k; i++){
            curr += pref[i];
            mini = min(mini, curr);
        }
        return mini;
    }
};
