class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), m = *max_element(nums.begin(), nums.end());
        vector<int> cnt(m + 1);
        for(int i = 0; i < n - 1 - i; ++i) {
            cnt[abs(nums[i] - nums[n - 1 - i])]++;
            //cout<<abs(nums[i] - nums[n - 1 - i])<<" ";
        }
        //cout<<endl;
        stack<int> s;//x
        for(int i = 0; i < m + 1; ++i) {
            if(cnt[i] == 0) continue;
            if(s.empty() || cnt[s.top()] < cnt[i]) s.push(i);

        }
        int ans = INT_MAX;
        while(s.size()) {
            int x = s.top(), t = 0;
            s.pop();
            for(int i = 0; i < n - i - 1; ++i) {
                if(abs(nums[i] - nums[n - i - 1]) == x) continue;
                if(min(nums[i], nums[n - i - 1]) + x > k && max(nums[i], nums[n - i - 1]) - x < 0) t += 2;
                else t += 1;
            }
            ans = min(ans, t);
            //cout<<x<<" "<<t<<endl;
        }
        return ans;
    }
};
