class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        diff = [0]*(k+2)

        def update(x, y, d):
            if x > y:
                return
            nonlocal diff
            diff[x] += d
            diff[y+1] -= d

        for i in range(n//2):
            li = [nums[i], nums[n-i-1]]
            li.sort()
            a, b = li
            t = b-a
            update(0, t-1, 1)
            update(t+1, t+max(a, k-b), 1)
            update(t+max(a, k-b)+1, k, 2)

        for i in range(1, k+2):
            diff[i] += diff[i-1]

        print(diff)
        return min(diff[:-1])
