class Solution:

    def solve(self, lst, n, target, k):

        rslt = 0

        for x in range(n):
            if lst[x] - lst[- x - 1] == target:
                continue
            if lst[x] - target >= 0:
                r = 1
            elif lst[- x - 1] + target <= k:
                r = 1
            else:
                r = 2
            rslt += r

        return rslt

    def minChanges(self, nums: List[int], k: int) -> int:

        lst = []
        n = len(nums)

        for x in range(n // 2):
            if nums[x] < nums[- x - 1]:
                nums[x], nums[- x - 1] = nums[- x - 1], nums[x]
            lst.append(nums[x] - abs(nums[- x - 1]))

        Hash = defaultdict(int)

        for x in lst:
            Hash[x] += 1

        rslt = 10000000000

        Hash1 = defaultdict(lambda : 1000000)

        for x in Hash:
            Hash1[Hash[x]] = min(Hash1[Hash[x]], x)

        # print(Hash1)
        n //= 2

        for x in Hash1:
            rslt = min(rslt, self.solve(nums, n, Hash1[x], k))

        return rslt
