class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, int> m;
        for(int i = 0, j = nums.size()-1; i < j; i++, j--){
            m[abs(nums[i]-nums[j])]++;
        }
        int r = INT_MAX;
        int temp = 0, t = 0, db = 0, c = 0;
        priority_queue<vector<int>> pq;

        for(auto&[key, val]: m){
            pq.push({val, key});
        }
        do{
            temp = pq.top()[0], t = pq.top()[1], db = 0, c = 0;
            pq.pop();

            for(int i = 0, j = nums.size()-1; i < j; i++, j--){
                if(abs(nums[i]-nums[j]) != t){
                    if(k - nums[i] < t && nums[i] < t && k - nums[j] < t && nums[j] < t)c++, db++;
                    c++;
                }
            }
            r = min(r, c);
        }while(pq.size() && temp - db <= pq.top()[0]);

        return r;
    }
};
