class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        cnts = Counter()
        for i in range(n//2):
            cnts[abs(nums[i] - nums[n-i-1])] += 1
        ans = inf
        for x , _ in sorted(cnts.items(),key=lambda x:-x[1]):
            t = 0
            y = 0
            for i in range(n//2):
                a = nums[i]
                b = nums[n-i-1]
                if abs(a-b) == x:continue
                if a < x and b < x and a + x > k and b + x > k:
                    t += 2
                    y += 1
                else:
                    t += 1
            ans = min(ans,t)
            if y == 0:return ans
        return ans
