class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), ans = INT_MAX;
        vector<pair<int, int>> a(n / 2);
        unordered_map<int, int> cnt;
        for (int i = 0; i < n / 2; ++i) {
            int mx = max(nums[i], nums[n - 1 - i]), mn = min(nums[i], nums[n - 1 - i]);
            a[i] = {mx - mn, max(k - mn, mx)};
            ++cnt[mx - mn];
        }
        ranges::sort(a);
        m = k + 1;
        bit.resize(k + 2);
        for (int x = 0, i = 0; x <= k; ++x) {
            while (i < a.size() && a[i].first < x) {
                add(a[i].second + 1);
                ++i;
            }
            ans = min(ans, n / 2 - cnt[x] + sum(x));
        }
        return ans;
    }
private:
    int m;
    vector<int> bit;
    int lowbit(int i) {
        return i & -i;
    }

    void add(int i) {
        while (i <= m) {
            ++bit[i];
            i += lowbit(i);
        }
    }

    int sum(int i) {
        int s = 0;
        while (i > 0) {
            s += bit[i];
            i -= lowbit(i);
        }
        return s;
    }
};
