class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int>s(k+1),c(k+2);
        map<int,int>mp;
        for(int i=0;i<a.size()/2;i++){
            int del = abs(a[i]-a[a.size()-i-1]);
            int temp=min({a[i],a[a.size()-i-1],k-a[i],k-a[a.size()-i-1]});
            mp[del]++;
            c[k-temp+1]++;
        }
        for(int i=1;i<=k+1;i++){
            c[i]+=c[i-1];
        }
        int n=a.size()/2;
        int ans=100000000;
        for(auto [a,b]:mp){
            int cnt = n-b+c[a];
            // cout<<n<<" "<<b<<" "<<c[a]<<" "<<a<<endl;
            ans=min(ans,cnt);
        }
        return ans;
    }
};
