class Solution {
public:
    int bin_search(vector<pair<int,int>> &diff , int val)
    {
        int left = 0;
        int right = diff.size()-1;
        int pos = -1;
        while (left <= right)
        {
            int mid = (left+right)/2;
            if (diff[mid].first < val)
            {
                pos = mid;
                left = mid+1;
            }
            else if (diff[mid].first >= val)
            {
                right = mid-1;
            }

        }
        return pos;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<pair<int,int>>diff(n/2);
        vector<int>freq(k+1,0);
        for (int i = 0 ; i < n/2 ; i++)
        {
            int val = abs(k-min(nums[i],nums[n-i-1]));
            int val2 = abs(max(nums[i],nums[n-i-1]));
            diff[i].first = max(val,val2);
            diff[i].second = abs(nums[i]-nums[n-i-1]);
            freq[diff[i].second]++;

        }
        sort(diff.begin(),diff.end());
        int ans = 10000000;
        for (int i = 0 ; i <= k ; i++)
        {
            int val = 0;
            int count = freq[i];
            int pos = bin_search(diff,i);
            val+=(pos+1)*2;
            val+=(n/2-pos-count-1);
            ans = min(ans,val);
            // cout << pos << " " << val << " ";
        }
        // cout << endl;
        return ans;

    }
};
