class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        // [R, L]
        vector<pair<int, int> > vec;

        map<int, int> mp;

        int j = n - 1;
        for(int i = 0 ; i < n ; i++){
            if(j < i) break;
            int diff = abs(nums[i] - nums[j]);
            if(mp.find(diff) == mp.end()) mp[diff] = 0;
            mp[diff]++;
            int two = max(nums[i], k - nums[i]);
            two = max(two, max(nums[j], k - nums[j]));

            vec.push_back({0, 1});
            vec.push_back({two + 1, -1});
            j--;
        }

        sort(vec.begin(), vec.end());
        int cur_index = 0;
        int summ = 0;
        int ans = -1;
        for(auto x : mp){
            int diff = x.first;
            int cnt = x.second;
            //cout<<"okay diff "<<diff<<"  "<<cnt<<endl;
            while(cur_index < vec.size()){
                if(vec[cur_index].first <= diff){
                    summ += vec[cur_index].second;
                    cur_index++;
                }
                else{
                    break;
                }
            }
           // cout<<"curIndex is "<<cur_index<<"  "<<summ<<endl;
            int left = summ - cnt;
            int outer = (n/2) - summ;
           // cout<<"okay venn "<<cnt<<" "<<left<<" "<<n<<"  "<<summ<<endl;
            if(ans == -1){
                ans = 2*outer + left;
            }
            else{
                ans = min(ans, 2*outer + left);
            }

        }
        return ans;


    }
};
