class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        vector<int> b((int)(4e5));
        for(int i = 0; i < n / 2; i ++ ){
            int l1 = 0, r1 = 0;
            if(a[i] >= 0 && a[i] <= k){
                l1 = 0;
                r1 = max(k - a[i], a[i]);
            }
            else{
                l1 = min(abs(0 - a[i]), abs(k - a[i]));
                r1 = max(abs(0 - a[i]), abs(k - a[i]));
            }
            int l2 = 0, r2 = 0;
            if(a[n - i - 1] >= 0 && a[n - i - 1] <= k){
                l2 = 0;
                r2 = max(k - a[n - i - 1], a[n - i - 1]);
            }
            else{
                l2 = min(abs(0 - a[n - i - 1]), abs(k - a[n - i - 1]));
                r2 = max(abs(0 - a[n - i - 1]), abs(k - a[n - i - 1]));
            }
            int t = abs(a[i] - a[n - i - 1]);
            if(l1 > l2){
                swap(l1, l2);
                swap(r1, r2);
            }
            bool ok = 0;
            if(r1 >= l2){
                b[l1] --;
                b[max(r1, r2) + 1] ++;
                if(l1 <= t && r2 >= t) b[t] --, b[t + 1] ++, ok = 1;
            }
            else{
                b[l1] --;
                b[r1 + 1] ++;
                b[l2] --;
                b[r2 + 1] ++;
                if(l1 <= t && r1 >= t || l2 <= t && r2 >= t) b[t] --, b[t + 1] ++, ok = 1;
            }
            if(!ok){
                b[t] -= 2;
                b[t + 1] += 2;
            }
        }
        int res = n;
        for(int i = 0; i <= 3e5; i ++ ){
            if(i) b[i] += b[i - 1];
            res = min(res, n + b[i]);
        }
        return res;
    }
};
