int f(int a,int b)
{
    return a>b?a-b:b-a;
}
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int a[k+1];
        int n=nums.size();
        memset(a,0,sizeof a);
        a[0]=n;
        for(int i=0;i<n/2;i++)
        {
            int x=nums[i],y=nums[n-i-1];
            int len=max(x,k-x);
            len=max(len,max(y,k-y));
            a[0]--;
            if(len<k)
                a[len+1]++;
            int xx=f(x,y);
            a[xx]--;
            if(xx<k)
                a[xx+1]++;
        }
        int ans=a[0];
        for(int i=1;i<=k;i++)
        {
            a[i]+=a[i-1];
            ans=min(ans,a[i]);
        }
        //for(int i=0;i<=k;i++)
            //cout<<a[i]<<" ";
        return ans;

    }
};
