class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        vector<int> diff(1e5+3);
        int n = nums.size();
        for (int i = 0; i < nums.size() / 2; i += 1) {
            int d = abs(nums[i] - nums[n - i - 1]);
            diff[d] += 1;
            diff[d + 1] -= 1;
            int max_val = max({abs(nums[i] - k), abs(nums[n - i - 1] - k), nums[i], nums[n - i - 1]});
            int min_val = min({abs(nums[i] - k), abs(nums[n - i - 1] - k)});
            if (nums[i] <= k || nums[n - i - 1] <= k) {
                min_val = 0;
            }
            diff[min_val] += 1;
            diff[max_val + 1] -= 1;
            if (d > max_val || d < min_val) {
                diff[d] += 1;
                diff[d + 1] -= 1;
            }
            // cout << "i: " << i << " " << max_val << " " << min_val << " d: " << d << endl;
        }
        int cnt = nums.size(), ans = nums.size();
        for (int i = 0; i < diff.size(); i += 1) {
            cnt -= diff[i];
            ans = min(cnt, ans);
        }
        return ans;
    }
};
