class Solution {
public:
int cal(int n,int uu,vector<int>& nums,int tmp,int k)
{
        int ans=0;
        for(int i=0,j=n-1;i<j;i++,j--)
        {
            int m=max(nums[i],nums[j]),mm=min(nums[i],nums[j]);
            if(m<tmp&&k-mm<tmp)ans++;
        }
        // cout<<(n-uu*2)/2+ans<<endl;
        return (n-uu*2)/2+ans;
}
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int uu=0;
        map<int,int>mp;
        for(int i=0,j=n-1;i<j;i++,j--)
        {
            mp[abs(abs(nums[i]-nums[j]))]++;
        }
        for(auto [i,j]:mp)uu=max(uu,j);
        int tmp;
        vector<pair<int,int>>v;
        for(auto [i,j]:mp)v.push_back({j,i});
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        int ans=1e9;
        for(int i=0;i<min((int)v.size(),20);i++)
        {
            ans=min(ans,cal(n,v[i].first,nums,v[i].second,k));
        }
        return ans;
    }
};
