const int N = 100010;
int diff[N];

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        memset(diff, 0, sizeof(diff));
        int l = 0, r = n - 1;
        while(l < r) {
            int delta = abs(nums[r] - nums[l]);
            int mn = min(nums[l], nums[r]), mx = max(nums[l], nums[r]);
            // [0,max(mx,k-mn)]中除了delta只需要一次
            int right = max(mx, k - mn);
            diff[0]++, diff[delta]--;
            diff[delta + 1]++, diff[right + 1]--;
            diff[right + 1] += 2, diff[k + 1] -= 2;
            l++, r--;
        }
        int ans = diff[0];
        for(int i = 1; i <= k; i++) {
            diff[i] += diff[i - 1];
            ans = min(ans, diff[i]);
        }
        return ans;
    }
};
