func minChanges(nums []int, k int) int {
    n := len(nums)
    m := n / 2
    pairs := make([][]int, m)
    for i := range m {
        pairs[i] = make([]int, 2)
        pairs[i][0] = nums[i]
        pairs[i][1] = nums[n-i-1]
        sort.Ints(pairs[i])
    }


    safeDist := make([]int, m)
    for i := range m {
        safeDist[i] = max(k - pairs[i][0], pairs[i][1])
    }
    sort.Ints(safeDist)


    deltaFreq := map[int]int{}
    for i := range safeDist {
        deltaFreq[pairs[i][1] - pairs[i][0]]++
    }

    // fmt.Println(pairs)
    // fmt.Println(safeDist)
    // fmt.Println(deltaFreq)
    // fmt.Println()

    minChanges := n
    for delta := range deltaFreq {
        unsafeCnt := sort.Search(m, func(i int) bool {
            return safeDist[i] >= delta
        })
        safeCnt := m - unsafeCnt

        changes := unsafeCnt * 2 + safeCnt * 1 - deltaFreq[delta]

        minChanges = min(minChanges, changes)
    }

    return minChanges
}
