class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>m;
        int n=nums.size();
        int i=0,j=n-1;
        while(i<j){
          m[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        map<int,vector<int>,greater<int>>mp;
        for(auto j:m){
            // cout<<j.first<<" "<<j.second<<" ";
          mp[j.second].push_back(j.first);
        }
        for(auto j:mp){
         sort(j.second.begin(),j.second.end());
        }

        int ans=INT_MAX;
        for(auto j:mp){
            int a=j.second[0];
            int a1=0,a2=n-1;
            int t=0;
            // cout<<a<<" ";
            while(a1<a2){
              if(abs(nums[a1]-nums[a2])!=a){
                  // cout<<t<<" ";
                 int b1=abs(nums[a1]-0);
                  int b2=abs(nums[a1]-k);
                  int b3=abs(nums[a2]-0);
                  int b4=abs(nums[a2]-k);
                  int b=max(b1,max(b2,max(b3,b4)));
                  if(a<=b){
                     t++;
                  }
                  else{
                       t+=2;
                   }
              }
                a1++;
                a2--;
            }
            ans=min(ans,t);
        }
        return ans;

    }
};
